import { Table } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import {changeName} from './../store'

function Cart(){
    let a = useSelector((state)=>{return state})
    let dispatch = useDispatch()
    return (
        <div>
            <h6>{a.user.name}의 장바구니</h6>
            <Table>
            <thead>
                <tr>
                <th>#</th>
                <th>상품명</th>
                <th>수량</th>
                <th>변경하기</th>
                </tr>
            </thead>
            {
            a.cart.map(function(item, i){
              return(
                <tbody>
                <tr>
                <td>{i}</td>
                <td>{item.name}</td>
                <td>{item.count}</td>
                <td>
                    <button onClick={()=>{
                        dispatch(changeName())
                    }}> + </button>
                </td>
                </tr>
                </tbody>
              )
            })
          }
            </Table> 
        </div>
    )
}
export default Cart
