import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Navbar, Container, Nav} from 'react-bootstrap'
import { Routes, Route, useNavigate, Link, Outlet } from 'react-router-dom'
import bg from './img/bg.png'
import { useEffect, useState } from 'react';
import Detail from './routes/Detail'
import data from './data.js';
import axios from 'axios';

function App() {
  let [shoes, shoesSet] = useState(data);
  let [alertFlag, setAlert] = useState(false)
  let [count, setCount] = useState(0);
  let [tap, setTap] = useState(0);
  let navigate = useNavigate();

  return (
    <div className="App">
       <Navbar bg="dark" variant="dark">
        <Container>
        <Navbar.Brand href="#home">ShoesShop</Navbar.Brand>
        <Nav className="me-auto">
          <Nav.Link href="#home">Home</Nav.Link>
          <Nav.Link onClick={()=>navigate('/detail')}>Detail</Nav.Link>
        </Nav>
        </Container>
      </Navbar>

      <Link to="/">home</Link>
      <br/>
      <Link to="/detail">detail</Link>

      <Routes>
        <Route path='/' element={ 
        <>
        <div className="main-bg" style={{backgroundImage: 'url(' + bg + ')'}}></div>
        <div className="container">
        {
            alertFlag == true
            ? <div className="alert alert-warning">
                loading....
            </div>
            : null
        }
        <div className="row">
        {
            shoes.map(function(item, i){
              return(
                <Card item={item} i={i+1} ></Card>
              )
            })
          }
        </div>
        <button onClick={()=>{
          setAlert(true)
          count = count + 1;
          setCount(count);
          if(count === 2){
            alert('no')
          }
          axios.get('https://codingapple1.github.io/shop/data2.json').then((결과)=>{
            console.log(결과.data)
            setAlert(false)
            let copy = [...shoes, ...결과.data]
            shoesSet(copy);
          })
          Promise.all([axios.get('/url1'),axios.get('/url2')]) //여러개 날릴때
          fetch('URL').then(결과 => 결과.json()).then((결과) => { console.log(결과) } )
          .catch(()=>{
            console.log('실패함')
          })
        }}>button</button>
        <Nav variant="tabs"  defaultActiveKey="link0">
    <Nav.Item>
      <Nav.Link eventKey="link0" onClick={()=>setTap(0)}>버튼0</Nav.Link>
    </Nav.Item>
    <Nav.Item>
      <Nav.Link eventKey="link1"onClick={()=>setTap(1)}>버튼1</Nav.Link>
    </Nav.Item>
    <Nav.Item>
      <Nav.Link eventKey="link2"onClick={()=>setTap(2)}>버튼2</Nav.Link>
    </Nav.Item>
    </Nav>
    <TabContent tap={tap}/>
        </div>
        </>} />
        <Route path='/detail/:id' element={ <Detail shoes={shoes}/> } />

        <Route path='/about' element={ <About/> }>
          <Route path='member' element={<div>멤버임</div>} />
        </Route>
        <Route path='/event' element={ <Event/> }>
          <Route path="one" element={<p>첫 주문시 양배추즙 서비스</p>}></Route>
          <Route path="two" element={<p>생일기념 쿠폰받기</p>}></Route>
        </Route>
        <Route path='*' element={ <div>(404) 없는 페이지 입니다</div> } />
      </Routes>
    </div>
  );
}

function TabContent({tap}){
  useEffect(

  )
  return <div className='start'>내용{tap}</div>
}

function About(){
  return (
    <div>
      <h4>회사정보임</h4>
      <Outlet></Outlet>
    </div>
  )
}
function Event(){
  return (
    <div>
      <h4>오늘의 이벤트</h4>
      <Outlet></Outlet>
    </div>
  )
}
function Card(props){ //대분자로 만드삼
  return(
      <div className="col-md-4">
        <img src={'https://codingapple1.github.io/shop/shoes' + props.i + '.jpg'} width="80%" />
        <h4>{props.item.title}</h4>
        <p>{props.item.content}</p>
        <p>{props.item.price}</p>
      </div>
  )
}

export default App;
