let data = [
    {
      id : 0,
      title : "White and Black",
      content : "Born in France",
      price : 120000,
      url: "https://codingapple1.github.io/shop/shoes1.jpg"
    },

    {
      id : 1,
      title : "Red Knit",
      content : "Born in Seoul",
      price : 110000,
      url: "https://codingapple1.github.io/shop/shoes2.jpg"
    },

    {
      id : 2,
      title : "Grey Yordan",
      content : "Born in the States",
      price : 130000,
      url: "https://codingapple1.github.io/shop/shoes3.jpg"
    }
  ] 
  let user = createSlice({
    name : 'user',
    initialState : {name: 'kim', age: 20} ,
    reducers: {
        changeName(state){
          state.name = 'park' //store의 state 변경하는 법
        },
        increase(state, action){
          state.age += action.payload
        }
    }
  })

export default data; //export 하려면  + 변수명
